mapper.select:
select save_folder, original_file, save_file
from file_info
where board_id = #{board_id}

mapper.update:
update board
set board_views = board_views + 1
where board_id = #{board_id}

update board
set board_title = #{board_title}, board_content = #{board_content}
where board_id = #{board_id}
